import "./Ownable.sol";
import "./provableAPI.sol";
import "./Destroyable.sol";
import "./SafeMath.sol";
pragma solidity 0.5.12;


contract CoinFlip is Ownable, Destroyable, usingProvable {


using SafeMath for uint256;

struct Player {
uint bet;
uint guess;
uint outcome;
bytes32 queryId;
bool awaitingCallback;

}


 uint256 constant NUM_RANDOM_BYTES_REQUESTED = 1;

uint private spend = 1 ether;

uint public outcome = 0;

uint public balance;

mapping (address => Player) private players;


event playersOutcome(uint value, string outcome);
event LogNewProvableQuery(string description);
event generatedRandomNumber(uint256 randomNumber);

 modifier minimumCost(uint cost) {
     require(msg.value >= spend, "Must send at least 1 ether to the contract");
     _;
}

 constructor() public {
     balance = 0;

 }


 /* function random() private returns(uint){
     return outcome = now % 2;
 } */

 function __callback(bytes32 _queryId, string memory _result, bytes memory _proof) public {
  require(msg.sender == provable_cbAddress());

      uint256 randomNumber = uint256(keccak256(abi.encodePacked(_result))) % 2;

      players[msg.sender].outcome = randomNumber;

      if(players[msg.sender].awaitingCallback){
        finaliseOutcome(players[msg.sender].outcome);
        emit generatedRandomNumber(randomNumber);
        delete players[msg.sender];

      }
}

function update() payable public
{
  uint256 QUERY_EXECUTION_DELAY = 0; // NOTE: The datasource currently does not support delays > 0!
  uint256 GAS_FOR_CALLBACK = 200000;

  //FOR PRODUCTION
  /* bytes32 queryId = provable_newRandomDSQuery(
      QUERY_EXECUTION_DELAY,
      NUM_RANDOM_BYTES_REQUESTED,
      GAS_FOR_CALLBACK
  ); */

  //FOR TEST
  bytes32 queryId = testRandom();

  players[msg.sender].queryId = queryId;

  emit LogNewProvableQuery("Provable query was sent, standing by for the answer...");

}

//FOR TEST PURPOSES
function testRandom() public returns(bytes32){
  bytes32 queryId = bytes32(keccak256("test"));
  __callback(queryId, "1", bytes("test"));
  return queryId;

}

function guess(uint number) public payable minimumCost(spend) {
    require(number == 0 || number == 1, "please pick a a whole number between 0 and 1");
    require(balance >= msg.value, "contract has insufficient funds for a payout in the event of a win");

    Player memory p;

    p.bet = msg.value;
    p.guess = number;
    p.awaitingCallback = true;

    insertPlayer(p);

    balance = balance.add(p.bet);

    update();

}

function insertPlayer(Player memory player) private {
  players[msg.sender] = player;
}


function finaliseOutcome(uint res) private {
  uint expected = players[msg.sender].guess;

  if(expected == res){

      uint winnings = players[msg.sender].bet.mul(2);
      msg.sender.transfer(winnings);
      balance -= winnings;
      assert(expected == res);
      emit playersOutcome(winnings, "Congratulations! You have doubled your stake!");

  } else {
    assert(expected != res);
    emit playersOutcome(players[msg.sender].bet , "You have lost your stake!");
  }

    players[msg.sender].awaitingCallback = false;

  assert(outcome == 0 || outcome == 1);
  assert(balance >= players[msg.sender].bet);

}

function addLiquidity() public payable minimumCost(spend) onlyOwner returns(uint) {

    uint old_balance = balance;

    balance += msg.value;

    assert(balance > old_balance);

    return balance;

}


}
